    private void btnKgLbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnKgLbActionPerformed
        // Instanciar la clase Conversiones
        Conversiones conversion = new Conversiones();
    
        double valor = Double.parseDouble(txtValor.getText());
        double resultado = chkInversa.isSelected() 
            ? conversion.lbToKg(valor) 
            : conversion.kgToLb(valor);
    
        lblResultado.setText(String.format("%.2f %s", resultado, chkInversa.isSelected() ? "Kg" : "Lb"));
    }//GEN-LAST:event_btnKgLbActionPerformed

    private void btnSolDolActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSolDolActionPerformed
        // Instanciar la clase Conversiones
        Conversiones conversion = new Conversiones();
    
        double valor = Double.parseDouble(txtValor.getText());
        double tasaCambio = 3.5;

        double resultado = chkInversa.isSelected() 
            ? conversion.dolarToSol(valor, tasaCambio) 
            : conversion.solToDolar(valor, tasaCambio);
    
        String moneda = chkInversa.isSelected() ? "Sol" : "USD";
        lblResultado.setText(String.format("%.2f %s", resultado, moneda));
    }//GEN-LAST:event_btnSolDolActionPerformed

    private void btnCaFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCaFActionPerformed
        // Instanciar la clase Conversiones
        Conversiones conversion = new Conversiones();
    
        double valor = Double.parseDouble(txtValor.getText());

        double resultado = chkInversa.isSelected() 
            ? conversion.fahrenheitToCelsius(valor) 
            : conversion.celsiusToFahrenheit(valor);
    
        String unidad = chkInversa.isSelected() ? "C째" : "F째";
        lblResultado.setText(String.format("%.2f %s", resultado, unidad));
    
    }//GEN-LAST:event_btnCaFActionPerformed

    private void btnFeetMtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFeetMtActionPerformed
        // Instanciar la clase Conversiones
        Conversiones conversion = new Conversiones();
    
        double valor = Double.parseDouble(txtValor.getText());

        double resultado = chkInversa.isSelected() 
            ? conversion.feetToMeters(valor) 
            : conversion.metersToFeet(valor);
    
        String unidad = chkInversa.isSelected() ? "Metros" : "Feet";
        lblResultado.setText(String.format("%.2f %s", resultado, unidad));
    }//GEN-LAST:event_btnFeetMtActionPerformed

